function replaceTextByLanguage(t){Object.keys(t).forEach(e=>{const n=t[e];n.forEach(t=>{const n=document.querySelectorAll(".lang-item :is(span, div)");n.forEach(n=>{n.textContent.includes(t)&&(n.textContent=e)})})})}const languageObject={en:["Englisch","English"],de:["Deutsch","German"]};replaceTextByLanguage(languageObject),document.addEventListener("DOMContentLoaded",function(){const t=document.querySelectorAll(".language-nav");t.forEach(t=>{const e=t.querySelectorAll(".lang-item a");e.forEach(t=>{t.setAttribute("tooltip","Switch Language"),tippy(t,{content:t.getAttribute("tooltip"),placement:"bottom",arrow:!1,offset:[0,5]})})})});