async function switchView(t){if(!document.startViewTransition)return void await updateTheDOM(t);const e=document.startViewTransition(async()=>{await updateTheDOM(t)});await animateFromMiddle(e),await e.ready}async function animateFromMiddle(t){try{await t.ready,document.documentElement.animate({transform:["scale(1)","scale(1.2)"]},{duration:500,easing:"ease-in-out"})}catch(t){console.error("Animation error:",t)}}async function updateTheDOM(t){const e=document.getElementById("content");e?(await new Promise(t=>setTimeout(t,100)),e.innerHTML=t):console.warn("Content element not found")}